class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = []
        for cost in horizontalCut:
            cuts.append((cost, 0))
        for cost in verticalCut:
            cuts.append((cost, 1))
        cuts.sort(reverse = True)
        # print(cuts)

        ans = 0
        count_h = 1
        count_v = 1
        for cost, direction in cuts:
            if direction == 0:
                ans += cost * count_v
                count_h += 1
            else:
                ans += cost * count_h
                count_v += 1
        return ans
