class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        int hp = 1, vp = 1;
        sort(hor.begin(), hor.end());
        sort(ver.begin(), ver.end());
        int i = m - 2, j = n - 2;
        long long cost = 0;
        while(i >= 0 && j >= 0){
            if(hor[i] > ver[j]){
                cost += vp * hor[i];
                hp++;
                i--;
            }
            else{
                cost += hp * ver[j];
                vp++;
                j--;
            }
        }
        while(i >= 0){
            cost += vp * hor[i];
            i--;
        }
        while(j >= 0){
            cost += hp * ver[j];
            j--;
        }
        return cost;
    }
};
