typedef long long LL ;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& ht, vector<int>& vt) {
         priority_queue<LL> q1 ;
        priority_queue<LL> q2 ;
        for(int x : ht) q1.push(x) ;
        for(int x : vt) q2.push(x) ;
        LL ans = 0 ;
        int h =  1  , v = 1 ;
        while(!q1.empty() || !q2.empty()){
            if(!q1.empty() && !q2.empty()){
                LL x = q1.top()  , y = q2.top() ;
                if(x>y){
                    q1.pop() ;
                    h++ ;
                    ans += 1LL * x * v ;
                }else{
                    q2.pop() ;
                    v++ ;
                    ans +=1LL *  y * h ;
                }
            }else if(q1.empty() && !q2.empty()){
                int y = q2.top() ;
                q2.pop() ;
                v++ ;
                ans += 1LL * y * h ;
            }else if(!q1.empty() && q2.empty()){
                int x = q1.top() ;
                q1.pop() ;
                h++ ;
                ans += 1LL * x * v ;
            }else{
                break ;
            }
        }
        return ans ;
    }
};
