class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        using ll = long long;
        vector<pair<ll, int>> all;
        for(int i = 0; i + 1 < m; i++) all.emplace_back(h[i], 0);
        for(int i = 0; i + 1 < n; i++) all.emplace_back(v[i], 1);
        sort(all.rbegin(), all.rend());
        vector<ll> cnt(2, 1);
        ll ans = 0;
        for(auto [k, v] : all){
            ans += cnt[!v] * k;
            cnt[v] += 1;
        }
        return ans;
    }
};
