/**
 * @param {number} m
 * @param {number} n
 * @param {number[]} horizontalCut
 * @param {number[]} verticalCut
 * @return {number}
 */
var minimumCost = function(m, n, horizontalCut, verticalCut) {

    let horizontalCuts = 1
    let verticalCuts = 1

    verticalCut.sort((a,b) => a-b)
    horizontalCut.sort((a,b) => a-b)

    let sum = 0;

    while(horizontalCut.length || verticalCut.length) {
        const last1 = horizontalCut.length - 1
        const last2 = verticalCut.length - 1
        if(last1 >= 0 && last2 >= 0 && horizontalCut[last1] >= verticalCut[last2]) {
            sum += (horizontalCuts * horizontalCut.pop())
            verticalCuts++
        } else if(last1 >= 0 && last2 >= 0 && horizontalCut[last1] < verticalCut[last2]) {
            sum += (verticalCuts * verticalCut.pop())
            horizontalCuts++
        } else if(last1 >= 0) {
            sum += (horizontalCuts * horizontalCut.pop())
            verticalCuts++
        } else if(last2 >= 0) {
            sum += (verticalCuts * verticalCut.pop())
            horizontalCuts++
        }
    }

    return sum
};
