class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        costs = []
        for hc in horizontalCut:
            costs.append((hc, 'H'))
        for vc in verticalCut:
            costs.append((vc, 'V'))

        costs.sort(reverse=True)

        ret = 0
        H = 0
        V = 0

        for c, t in costs:
            if t == 'V':
                V += 1
                ret += c * (H + 1)
            else:
                H += 1
                ret += c * (V + 1)

        return ret
