class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        priority_queue<pair<long long int, long long int>> pq;
        for(auto i : hc)
        {
            pq.push(make_pair(i, 1));
        }
        for(auto i : vc)
        {
            pq.push(make_pair(i, 2));
        }
        long long int h = 1, v = 1;
        long long int res = 0;
        while(!pq.empty())
        {
            auto temp = pq.top();
            pq.pop();
            if(temp.second==2) {res+=temp.first*v;h++;}
            else {res+=temp.first*h;v++;}
        }
        return res;
    }
};
