class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hor = sorted(horizontalCut,reverse=True)
        ver = sorted(verticalCut, reverse=True)
        ans = 0
        lo = 0
        hi = 0
        hc = 1
        vc = 1
        while lo < len(hor) and hi < len(ver):
            if hor[lo] >= ver[hi]:
                ans += hor[lo]*hc
                vc+=1
                lo+=1
            else:
                ans+=ver[hi]*vc
                hc+=1
                hi+=1
        for i in range(lo,len(hor)):
            ans+=hor[i]*hc
        for i in range(hi,len(ver)):
            ans+=ver[i]*vc
        return ans
