class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort(reverse=True)
        verticalCut.sort(reverse=True)

        hq = deque(horizontalCut)
        vq = deque(verticalCut)

        mp=1
        np=1

        ans = 0

        while hq or vq:
            if hq and vq:
                if hq[0] > vq[0]:
                    ans += hq.popleft() * mp
                    np += 1
                else:
                    ans += vq.popleft() * np
                    mp += 1
            elif hq:
                ans += hq.popleft() * mp
            else:
                ans += vq.popleft() * np

        return ans
