class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int, int>> cut;
        for(int i = 0 ; i < n-1 ; i++) {
            cut.push_back({v[i], 1});
        }
        for(int i = 0 ; i < m-1 ; i++) {
            cut.push_back({h[i], 0});
        }

        sort(cut.begin(), cut.end());
        reverse(cut.begin(), cut.end());

        int cuth = 1, cutv = 1; long long ans = 0; 
        for(auto it : cut) {
            int cost = it.first;
            if(it.second == 0) {
                ans += ((1LL*cuth)*(1LL*cost));
                cutv++;
            }
            else{
                ans += ((1LL*cutv)*(1LL*cost));
                cuth++;
            }
        }
        return ans;
    }
};
