class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        long xCnt = 1, yCnt = 1;
        int i = m - 2, j = n - 2;
        long res = 0;
        while (i >= 0 || j >= 0) {
            if (i >= 0 && j >= 0) {
                if (horizontalCut[i] > verticalCut[j]) {
                    res += horizontalCut[i] * yCnt;
                    i--;
                    xCnt++;
                } else {
                    res += verticalCut[j] * xCnt;
                    j--;
                    yCnt++;
                }
            } else if (i >= 0) {
                res += horizontalCut[i] * yCnt;
                i--;
                xCnt++;
            } else {
                res += verticalCut[j] * xCnt;
                j--;
                yCnt++;
            }
        }
        return res;
    }
}
