class Solution:
    def minimumCost(self, m: int, n: int, hz: List[int], vt: List[int]) -> int:
        res = sum(hz) + sum(vt)
        hz.sort()
        vt.sort()
        for h in hz:
            res += (len(vt)-bisect_left(vt, h)) * h
        for v in vt:
            res += (len(hz)-bisect_right(hz, v)) * v
        return res
