from typing import List


class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ans = 0
        li = []
        for horizontal in horizontalCut:
            li.append((horizontal, 0))
        for vertical in verticalCut:
            li.append((vertical, 1))
        cnt0 = cnt1 = 0
        li.sort(reverse=True)
        for val, typ in li:
            if typ == 1:
                ans += val * (1 + cnt0)
                cnt1 += 1
            else:
                ans += val * (1 + cnt1)
                cnt0 += 1
        return ans
