class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hor = 1
        ver = 1
        ans = 0
        L = []
        L1 = []
        for i in horizontalCut:
            L.append(i)
        for i in verticalCut:
            L1.append(i)
        L.sort()
        L1.sort()
        while L and L1:
            if L[-1]<L1[-1]:
                f = L1.pop()
                ans += ver*f
                hor += 1
            else:
                f = L.pop()
                ans += hor*f
                ver += 1
        while L:
            f = L.pop()
            ans += hor*f
        while L1:
            f = L1.pop()
            ans += ver*f
        return ans
