class Solution {
public:
struct ComparePair {
    bool operator()(const pair<int, int>& p1, const pair<int, int>& p2) {
        if (p1.first == p2.first) {
            return p1.second < p2.second; // Compare second elements if first elements are    equal
        }
        return p1.first < p2.first; // Compare first elements
    }
};
    long long  minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<pair<int, int>, vector<pair<int, int>>, ComparePair> pq;
        for(int i=0;i<h.size();i++){
            pq.push({h[i],0});
        }
         for(int i=0;i<v.size();i++){
            pq.push({v[i],1});
        }
        int vc=1,hc=1;
        long long  s=0;
        while(!pq.empty()){
            int w=pq.top().first;
            int x=pq.top().second;
            pq.pop();
            if(x==0){
              s=s+w*vc;
              hc++;
            }else{
                s=s+w*hc;
                vc++;
            }
        }
        return s;

    }
};
