class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ar = []
        for val in horizontalCut:
            ar.append([val, 0])
        for val in verticalCut:
            ar.append([val, 1])
        ar.sort(reverse=1)
        res = 0
        c1, c2 = 0, 0
        for val,mark in ar:
            if not mark:
                res += (c2 + 1) * val
                c1 += 1
            else:
                res += (c1 + 1) * val
                c2 += 1
        return res
