class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& X, vector<int>& Y) {
        long long res = 0;

        sort(X.begin(), X.end(), greater<int>());
        sort(Y.begin(), Y.end(), greater<int>());

        int hzntl = 1, vert = 1;
        int i = 0, j = 0;

        while (i < m-1 && j < n-1) {
            if (X[i] > Y[j]) {
                res += X[i] * vert;
                hzntl++;
                i++;
            } else {
                res += Y[j] * hzntl;
                vert++;
                j++;
            }
        }

        while (i < m-1) {
            res += X[i++] * vert;
        }

        while (j < n-1) {
            res += Y[j++] * hzntl;
        }

        return res;
    }
};
