class Solution {
    public long minimumCost(int m, int n, int[] h, int[] v) {
        int hp =1 ;
        int vp =1;

        Arrays.sort(h);
        Arrays.sort(v);

        int i = h.length-1;
        int j = v.length-1;
        long ans =0;
        while(i>=0 && j>=0){
            if(h[i]>v[j]){
                ans += h[i]*vp;
                hp++;
                i--;
            }
            else{
                ans+=v[j]*hp;
                vp++;
                j--;
            }
        }

        while(i>=0){
            ans+=h[i]*vp;
            hp++;
            i--;
        }

        while(j>=0){
            ans+=v[j]*hp;
            vp++;
            j--;
        }

        return ans;

    }
}
