class Solution {
public:

    long long helper(vector<int>& a, vector<int>& b) {
    sort(a.begin(), a.end(), greater<int>());
    sort(b.begin(), b.end(), greater<int>());
    int x = 1;
    int y = 1;
    long long ans = 0;    
    int i = 0, j = 0;    
    while (i < a.size() && j < b.size()) {
        if (a[i] > b[j]) {
            ans += 1LL*a[i] * y;
            x++;
            i++;
        } else {
            ans += 1LL*b[j] * x;
            y++;
            j++;
        }
    }    
    while (i < a.size()) {
        ans += 1LL*a[i] * y;
        i++;
    }    
    while (j < b.size()) {
        ans += 1LL*b[j] * x;
        j++;
    }
    return ans;
}

    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        return helper(a,b);
    }
};
