using LL = long long;

class Solution {
public:
    long long minimumCost(int ny, int nx, vector<int>& y_cut, vector<int>& x_cut) {
        ranges::sort(y_cut);
        ranges::sort(x_cut);

        deque<LL> dqy(y_cut.begin(), y_cut.end());
        deque<LL> dqx(x_cut.begin(), x_cut.end());
        LL py = 1;
        LL px = 1;

        LL ans = 0;
        while (!dqy.empty() && !dqx.empty()) {
            if (dqy.back() < dqx.back()) {
                ans += dqx.back() * py;
                dqx.pop_back();
                ++px;
            } else {
                ans += dqy.back() * px;
                dqy.pop_back();
                ++py;
            }
        }
        while (!dqy.empty()) {
            ans += dqy.back() * px;
            dqy.pop_back();
            ++py;
        }
        while (!dqx.empty()) {
            ans += dqx.back() * py;
            dqx.pop_back();
            ++px;
        }
        return ans;
    }
};
