class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
         sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());
        #define int long long
        int i = 0;int j = 0;
        int ch = 1;
        int cv = 1;
        int ans = 0;
        while(i  + 1< m && j + 1 < n){
            if(h[i] > v[j]){
                ans += cv * h[i];
                ch++;
                i++;
            }
            else{
                ans += ch * v[j];
                cv++;
                j++;
            }
        }
        // cout<<ans<<endl;
        while(i  + 1< m){
            ans += cv * h[i];
            ch++;
            i++;
        }
        while(j  + 1< n){
            ans += ch * v[j];
            cv++;
            j++;
        }
        return ans;
        #undef int
    }
};
