class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans = 0;
        sort(v.rbegin(), v.rend());
        sort(h.rbegin(), h.rend());
        long long i=0, j=0;
        while(i<n-1 and j<m-1){
            if(v[i]>h[j]){
                ans += v[i++]*(j+1);
            } else if(h[j]>v[i]){
                ans += h[j++]*(i+1);
            } else {
                if(i>j){
                    ans += v[i++]*(j+1);
                } else {
                    ans += h[j++]*(i+1);
                }
            }
        }
        while(i<n-1){
            ans += v[i++]*m;
        }
        while(j<m-1){
            ans += h[j++]*n;
        }
        return ans;
    }
};
