class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        costs = []
        for val in horizontalCut:
            costs.append((val, 'H'))
        for val in verticalCut:
            costs.append((val, 'V'))
        costs.sort(reverse = True, key = lambda x: x[0])
        hP = 1
        vP = 1
        total = 0
        for cost, cType in costs:
            if cType == 'H':
                total += cost * vP
                hP += 1
            else:
                total += cost * hP
                vP += 1
        return total
