class Solution {
    public long minimumCost(int m, int n, int[] h, int[] v) {
        Arrays.sort(h);
        Arrays.sort(v);
        long res = 0;

        int i = m - 2, j = n - 2;
        long hsum = 0;
        long vsum = 0;
        for (int num : h) hsum += num;
        for (int num : v) vsum += num;
        while (i >= 0 && j >= 0) {
            if (h[i] >= v[j]) {
                // 切最大的横
                res += h[i] + vsum;
                hsum -= h[i--];
            } else {
                res += v[j] + hsum;
                vsum -= v[j--];
            }
        }
        if (i >= 0) {
            res += hsum;
        }
        if (j >= 0) res += vsum;
        return res;
    }
}
