class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        long long h = 1;
        long long v = 1;
        // , vector<pair<int, char>>, greater<pair<int, char>>
        // priority_queue<pair<long long, char>> pq;
        vector<pair<int,char>>pq;
        for (int i = 0; i < hc.size(); i++) {
            pq.push_back({hc[i], 'H'});
        }
        for (int i = 0; i < vc.size(); i++) {
            pq.push_back({vc[i], 'V'});
        }
        sort(pq.begin(),pq.end(),greater<pair<int, char>>());
        long long ans = 0;

        for(int i=0;i<pq.size();i++) {
            auto x = pq[i];
            // pq.pop();
            if (x.second == 'H') {
                h++;
                ans += x.first * v;
            } else {
                v++;
                ans += x.first * h;
            }
        }
        return ans;
    }
};
