class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        veritcal_count = 1
        horizontal_count = 1
        # [cost, is_horizontal]
        costs = [[n, True] for i, n in enumerate(horizontalCut)] + [[n, False] for i, n in enumerate(verticalCut)]
        costs.sort(reverse=True)
        res = 0
        for cost, is_horizontal in costs:
            if is_horizontal:
                res += cost * horizontal_count
                veritcal_count += 1
            else:
                res += cost * veritcal_count
                horizontal_count += 1

        return res
