#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<ll, ll>> ct;
        for (ll cost : horizontalCut) {
            ct.push_back({cost, 0});
        }
        for (ll cost : verticalCut) {
            ct.push_back({cost, 1});
        }
        sort(ct.rbegin(), ct.rend());
        ll hcuts = 1, vcuts = 1;
        ll tc = 0;

        for (auto& cut : ct) {
            if (cut.second == 0) {
                tc += cut.first * vcuts;
                hcuts++;
            } else {
                tc += cut.first * hcuts;
                vcuts++;
            }
        }

        return tc;
    }
};
