using ll = long long;
using vi = vector<ll>;
using vvi = vector<vi>;
using pii = pair<ll,ll>;
using vp = vector<pii>;
using ti3 = tuple<ll,ll,ll>;
using vti3 = vector<ti3>;
using vs = vector<string>;

template<typename T>
using min_pq = priority_queue<T, vector<T>, greater<T>>;
template<typename T>
using max_pq = priority_queue<T>;



class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& hc, vector<int>& vc) {
        ll rcnt = 0;
        ll ccnt = 0;
        vti3 a;
        for (int i=0; i<n-1; i++) {
            a.push_back({hc[i],i,0});
        }
        for (int i=0; i<m-1; i++) {
            a.push_back({vc[i],i,1});
        }
        sort(a.rbegin(), a.rend());
        ll ans = 0;
        for (auto [w,i,rc] : a) {
            if (rc == 0) {
                ans += w * (ccnt+1);
                rcnt++;
            }
            else {
                ans += w * (rcnt+1);
                ccnt++;
            }
        }
        return ans;
    }
};
