class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        total = 0
        cuts_remaining = list(zip(horizontalCut, ['h'] * (m - 1))) + list(zip(verticalCut, ['v'] * (n - 1)))
        cuts_remaining.sort(reverse = True)
        pieces = {'h': 1, 'v': 1}
        for cost, hv in cuts_remaining:
            total += pieces[hv] * cost
            pieces['h' if hv == 'v' else 'v'] += 1
        return total
