import java.util.Arrays;

class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        // Sort the cuts in descending order
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);

        // Use two pointers to traverse from the end of the sorted arrays
        int i = horizontalCut.length - 1;
        int j = verticalCut.length - 1;

        // Initialize the number of pieces created so far
        int horizontalPieces = 1;
        int verticalPieces = 1;

        long totalCost = 0;

        // Process the cuts starting from the most expensive
        while (i >= 0 && j >= 0) {
            if (horizontalCut[i] > verticalCut[j]) {
                totalCost += (long) horizontalCut[i] * verticalPieces;
                horizontalPieces++;
                i--;
            } else {
                totalCost += (long) verticalCut[j] * horizontalPieces;
                verticalPieces++;
                j--;
            }
        }

        // Process remaining horizontal cuts if any
        while (i >= 0) {
            totalCost += (long) horizontalCut[i] * verticalPieces;
            horizontalPieces++;
            i--;
        }

        // Process remaining vertical cuts if any
        while (j >= 0) {
            totalCost += (long) verticalCut[j] * horizontalPieces;
            verticalPieces++;
            j--;
        }

        return totalCost;
    }
}
