class Solution {
public:
    #define lll long long 
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        sort(begin(hor),end(hor),greater<int>());
        sort(begin(ver),end(ver),greater<int>());
        lll i = 0, j = 0 , rows = 1,cols=1,cost=0;
        while(i<hor.size() and j<ver.size()){
            if(hor[i]>ver[j]){
                cost+=hor[i++]*cols;
                rows++;
            }
            else{
                cost+=ver[j++]*rows;
                cols++;
            }
        }
        while(i<hor.size()){
            cost+=hor[i++]*cols;
            rows++;
        }
        while(j<ver.size()){
            cost+=ver[j++]*rows;
            cols++;
        }
        return cost;
    }
};
