import java.util.Arrays;

class Solution {
	public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
		Arrays.sort(horizontalCut);
		Arrays.sort(verticalCut);
		long horSum = 0, verSum = 0;
		for (int i = 0; i < m - 1; i++) {
			horSum += horizontalCut[i];
		}
		for (int i = 0; i < n - 1; i++) {
			verSum += verticalCut[i];
		}
		long res = horSum + verSum;
		int i = m - 2, j = n - 2;
		while (i >= 0 && j >= 0) {
			if (horizontalCut[i] >= verticalCut[j]) {
				res += verSum;
				horSum -= horizontalCut[i];
				i--;
			} else {
				res += horSum;
				verSum -= verticalCut[j];
				j--;
			}
		}
		return res;
	}
}
