typedef long long LL;

class Solution {
public:
    LL minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        sort(a.begin(), a.end(), greater<int>());
        sort(b.begin(), b.end(), greater<int>());

        int h = 0, v = 0, xh = 1, xv = 1;
        LL res = 0;
        while (h < a.size() && v < b.size()) 
        {
            if (a[h] > b[v]) {
                res += a[h] * xv;
                xh ++, h ++;
            } 
            else {
                res += b[v] * xh;
                xv ++;
                v ++;
            }
        }
        while (h < a.size()) 
        {
            res += a[h] * xv;
            xh ++, h ++;
        }
        while (v < b.size()) 
        {
            res += b[v] * xh;
            xv ++, v ++;
        }
        return res;
    }
};
