typedef long long ll;
constexpr ll MAXN = 1e5 + 5;

ll M, N;

ll psa[MAXN];

ll query(ll l, ll r) {
    return psa[r] - psa[l-1];
}

ll calc(ll y, ll j) {
    return j * y + query(j + 1, M - 1);
}

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end(), greater<ll>());
        sort(verticalCut.begin(), verticalCut.end(), greater<ll>());
        verticalCut.insert(verticalCut.begin(), 0ll);

        M = m;
        N = n;

        for (ll i = 1; i <= M - 1; i ++) {
            psa[i] = psa[i - 1] + horizontalCut[i - 1];
        }
        ll ans = 0;
        for (auto x: horizontalCut) ans += x;
        for (auto x: verticalCut) ans += x;

        for (ll Jnow = M - 1, i = N - 1; i >= 1; i --) {
            while (Jnow > 0 && calc(verticalCut[i], Jnow) > calc(verticalCut[i], Jnow - 1)) {
                Jnow --;
            }
            // cout << Jnow << ' ' << calc(verticalCut[i], Jnow) << '\n';
            ans += calc(verticalCut[i], Jnow);
        }

        return ans;
    }
};
