class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        int  i = n-2,j = m-2;
        int cnth = 1;
        int cntv = 1;
        long long ans = 0;
        while(i>=0 && j>=0){
            if(h[i]>v[j]){
                ans += (1ll*h[i]*cntv);
                cnth++;
                i--;
            }else{
                ans += (1ll*v[j]*cnth);
                cntv++;
                j--;
            }
        }
        while(i>=0){
            ans += (1ll*h[i]*cntv);
            cnth++;
            i--;
        }
        while(j>=0){
            ans += (1ll*v[j]*cnth);
            cntv++;
            j--;
        }
        return ans;
    }
};
