class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        sort(hcut.begin(), hcut.end(), [](int a, int b) {
            return a > b;
        });
        sort(vcut.begin(), vcut.end(), [](int a, int b) {
            return a > b;
        });

        int p1 = 0, p2 = 0;
        long long res = 0;

        while (p1 != m - 1 && p2 != n - 1) {
            if (hcut[p1] > vcut[p2]) {
                res += hcut[p1++] * (p2 + 1);
            } else {
                res += vcut[p2++] * (p1 + 1);
            }
        }
        while (p1 != m - 1) {
            res += hcut[p1++] * (p2 + 1);
        }
        while (p2 != n - 1) {
            res += vcut[p2++] * (p1 + 1);
        }

        return res;
    }
};
