class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hh, vector<int>& vv) {
        sort(hh.begin(),hh.end());
        sort(vv.begin(),vv.end());
        long long ans=0;
        for(int i=0, j=0;i<m-1;i++){
            while(j<n-1&&vv[j]<=hh[i]) j++;
            int c=n-1-j;
            ans+=(c+1)*hh[i];
        }

        for(int i=0, j=0;j<n-1;j++){
            while(i<m-1&&hh[i]<vv[j]) i++;
            int c=m-1-i;
            ans+=(c+1)*vv[j];
        }
        return ans;
    }
};
