class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans=0;
        priority_queue<pair<int,pair<int,int>>>q;

        for(int i=0;i<m-1;i++){
            q.push({h[i],{1,i}});
        }

        for(int i=0;i<n-1;i++){
            q.push({v[i],{2,i}});
        }

        long long cnt1=1,cnt2=1;

        while(!q.empty()){
            long long cost=q.top().first;
            int f=q.top().second.first;
            q.pop();

            if(f==1){
                cnt2++;
                ans+=(cnt1*cost);
            }
            else{
                ans+=(cnt2*cost);
                cnt1++;
            } 

        }
        return ans;
    }
};
