class Solution:
    def minimumCost(self, m: int, n: int, a: List[int], b: List[int]) -> int:
        cc = [[i, x] for i, c in enumerate([a, b]) for x in c]
        cc.sort(key=lambda x: -x[-1])
        cur = [1, 1]
        ans = 0
        for op, cost in cc: 
            ans += cur[1 - op] * cost
            cur[op] += 1 
        return ans
