class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.rbegin(), hc.rend()); sort(vc.rbegin(), vc.rend());

        long long ans = 0;
        for (int i=0, j=0; i<m-1 || j<n-1; ) {
            if (j == n-1 || (i < m-1 && hc[i] > vc[j])) {
                // cout << "h " << hc[i] << '\n';
                ans += 1LL * hc[i] * (j + 1);
                i++;
            } else {
                // cout << "v " << vc[j] << '\n';
                ans += 1LL * vc[j] * (i + 1);
                j++;
            }
        }

        return ans;
    }
};
