class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hC, vector<int>& vC) {
        sort(hC.begin(), hC.end());
        sort(vC.begin(), vC.end());

        long long h=1,v=1;
        int i=hC.size()-1,j=vC.size()-1;
        long long res=0;

        while(i>=0 && j >= 0)
        {
            if(hC[i]>vC[j])
            {
                res += hC[i]*v;
                h++;
                i--;
            }
            else
            {
                res += vC[j]*h;
                v++;
                j--;
            }
        }

        while(i>=0)
        {
            res += hC[i]*v;
            h++;
            i--;

        }

        while(j>=0)
        {
            res += vC[j]*h;
            v++;
            j--;

        }


        return res;
    }
};
