public class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        // Convert the arrays to Integer arrays for sorting in descending order
        Integer[] hCuts = Arrays.stream(horizontalCut).boxed().toArray(Integer[]::new);
        Integer[] vCuts = Arrays.stream(verticalCut).boxed().toArray(Integer[]::new);

        // Sort both arrays in descending order
        Arrays.sort(hCuts, Collections.reverseOrder());
        Arrays.sort(vCuts, Collections.reverseOrder());

        int hIndex = 0, vIndex = 0;
        int hSegments = 1, vSegments = 1;
        long totalCost = 0;

        // Use a greedy approach to always pick the larger cost cut first
        while (hIndex < hCuts.length && vIndex < vCuts.length) {
            if (hCuts[hIndex] >= vCuts[vIndex]) {
                totalCost += (long) hCuts[hIndex] * vSegments;
                hSegments++;
                hIndex++;
            } else {
                totalCost += (long) vCuts[vIndex] * hSegments;
                vSegments++;
                vIndex++;
            }
        }

        // Add remaining horizontal cuts
        while (hIndex < hCuts.length) {
            totalCost += (long) hCuts[hIndex] * vSegments;
            hIndex++;
        }

        // Add remaining vertical cuts
        while (vIndex < vCuts.length) {
            totalCost += (long) vCuts[vIndex] * hSegments;
            vIndex++;
        }

        return totalCost;
    }
}
