class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        int v=1;
        int h=1;
        int i=0;
        int j=0;
        long long int ans=0;
        sort(hor.rbegin(),hor.rend());
        sort(ver.rbegin(),ver.rend());
        while(i<m-1 && j<n-1){
            if(hor[i]>ver[j]){
                ans+=v*hor[i];
                i++;
                h++;
            }
            else{
                ans+=h*ver[j];
                j++;
                v++;
            }
        }
        while(i<m-1){
            ans+=v*hor[i];
            i++;
            h++;
        }
        while(j<n-1){
            ans+=h*ver[j];
            j++;
            v++;
        }
        return ans;
    }
};
