typedef long long ll;
typedef long double ld;
typedef __int128 i128;

class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& h, vector<int>& v) {
        sort(all(h));
        sort(all(v));
        ll rows = 1, cols = 1;
        ll ans = 0;
        while (sz(h) && sz(v)) {
            if (h.back() > v.back()) {
                ans += h.back() * cols;
                rows++;
                h.pop_back();
            } else {
                ans += v.back() * rows;
                cols++;
                v.pop_back();
            }
        }
        while (sz(h)) {
            ans += h.back() * cols;
            h.pop_back();
        }
        while (sz(v)) {
            ans += v.back() * rows;
            v.pop_back();
        }
        return ans;
    }
};
