class Solution:
    def minimumCost(self, m: int, n: int, hh: List[int], vv: List[int]) -> int:
        hh.sort(reverse=True)
        vv.sort(reverse=True)
        i=0
        j=0
        ans=0
        while i<m-1 and j<n-1:
            if hh[i]>vv[j]:
                ans+=hh[i]*(j+1)
                i+=1
            else:
                ans+=vv[j]*(i+1)
                j+=1
        while i<m-1:
            ans+=hh[i]*(j+1)
            i+=1
        while j<n-1:
            ans+=vv[j]*(i+1)
            j+=1
        return ans
