class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ho, ve = 1, 1
        horizontalCut.sort()
        verticalCut.sort()
        ans = 0
        while horizontalCut and verticalCut:
            if horizontalCut[-1] > verticalCut[-1]:
                ans += horizontalCut[-1] * ve
                horizontalCut.pop()
                ho += 1
            else:
                ans += verticalCut[-1] * ho
                verticalCut.pop()
                ve += 1
        if horizontalCut:
            ans += sum(horizontalCut) * ve
        else:
            ans += sum(verticalCut) * ho
        return ans
