class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        long hCount = 1, vCount = 1, totalCost = 0;
        int i = horizontalCut.length-1, j = verticalCut.length-1;
        while (i >=0 && j >= 0) {
            if (horizontalCut[i] > verticalCut[j]) {
                totalCost += horizontalCut[i--] * vCount;
                hCount++;
            } else {
                totalCost += verticalCut[j--] * hCount;
                vCount++;
            }
        }
        for (;i >= 0; i--) totalCost += horizontalCut[i] * vCount;
        for (;j >= 0; j--) totalCost += verticalCut[j] * hCount;
        return totalCost;
    }
}
