class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        int a=1, b=1;
        long res=0;
        List<int[]> list=new ArrayList<>();
        for (int i=0; i<horizontalCut.length; i++) {
            list.add(new int[]{horizontalCut[i], 0});
        }
        for (int i=0; i<verticalCut.length; i++) {
            list.add(new int[]{verticalCut[i], 1});
        }
        Collections.sort(list,(p, q)->q[0]-p[0]);
        for (int[] x:list) {
            if(x[1]==0) {
                res+=x[0]*b;
                a++;
            } 
            else{
                res+=x[0]*a;
                b++;
            }
        }
        return res;
    }
}
