class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        // Sort the cuts in descending order
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);

        int totalHorizontalCuts = m - 1;  // Number of horizontal cuts
        int totalVerticalCuts = n - 1;  // Number of vertical cuts

        int totalHorizontalSegments = 1;  // Number of horizontal segments initially
        int totalVerticalSegments = 1;  // Number of vertical segments initially

        int indexPointerForHorizontalCuts = totalHorizontalCuts - 1;  // Pointer for horizontal cuts
        int indexPointerForVerticalCuts = totalVerticalCuts - 1;  // Pointer for vertical cuts

        long totalCost = 0;  // Total cost initialization

        // Process the cuts in descending order
        while (indexPointerForHorizontalCuts >= 0 && indexPointerForVerticalCuts >= 0) {
            if (horizontalCut[indexPointerForHorizontalCuts] >= verticalCut[indexPointerForVerticalCuts]) {
                totalCost += (long) horizontalCut[indexPointerForHorizontalCuts] * totalVerticalSegments;
                totalHorizontalSegments++;
                indexPointerForHorizontalCuts--;
            } else {
                totalCost += (long) verticalCut[indexPointerForVerticalCuts] * totalHorizontalSegments;
                totalVerticalSegments++;
                indexPointerForVerticalCuts--;
            }
        }

        // Process remaining horizontal cuts
        while (indexPointerForHorizontalCuts >= 0) {
            totalCost += (long) horizontalCut[indexPointerForHorizontalCuts] * totalVerticalSegments;
            totalHorizontalSegments++;
            indexPointerForHorizontalCuts--;
        }

        // Process remaining vertical cuts
        while (indexPointerForVerticalCuts >= 0) {
            totalCost += (long) verticalCut[indexPointerForVerticalCuts] * totalHorizontalSegments;
            totalVerticalSegments++;
            indexPointerForVerticalCuts--;
        }

        return totalCost;
    }
}
