class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end()); 
        sort(v.begin(),v.end());  
        int i=m-2; 
        int j=n-2;
        long long ans=0; 
        int xcut=1,ycut=1; 
        while(i>=0 && j>=0) { 
            // cout<<i<<" "<<j<<endl;
            if(h[i] > v[j]) {
                // cout<<"h";
                ans+=1LL*ycut*h[i--]; 
                xcut++; 
            }
            else {
                // cout<<"v";
                ans+=1LL*xcut*v[j--]; 
                ycut++;
            }
        }
        while(i>=0) {
            ans+=1LL*ycut*h[i--]; 
            xcut++; 
        }
        while(j>=0) {
            ans+=1LL*xcut*v[j--]; 
            ycut++;
        }
        return ans;
    }
};
